import React, { useState, useEffect } from 'react'; // Importing useState from React
import Sidebar12 from "./sidebar";
import Content12 from "./component";
import Navbar12 from "./Snav";
import "./dd.css"
import { Link } from "react-router-dom";
import LogoutConfirmation from "./LogoutConfirmation";

function App12() {
    // const [selectedContent, setSelectedContent] = useState('');

    // const handleMenuItemClick = (content) => {
    //     setSelectedContent(content);
    // };

    const [selectedContent, setSelectedContent] = useState('');
    const [isLogoutConfirmationOpen, setIsLogoutConfirmationOpen] = useState(false);

    const handleMenuItemClick = (content) => {
        if (content === 'logout') {
            setIsLogoutConfirmationOpen(true);
        } else {
            setSelectedContent(content);
        }
    };

    const handleLogoutConfirm = () => {
        // Perform logout actions
        // For now, just redirect to login page
        window.location.href = '/login';
    };

    const handleLogoutCancel = () => {
        setIsLogoutConfirmationOpen(false);
    };

    useEffect(() => {
        const handleBackForward = (e) => {
            e.preventDefault();
            // Perform any desired forward action
        };

        window.addEventListener('popstate', handleBackForward);

        return () => {
            window.removeEventListener('popstate', handleBackForward);
        };
    }, []);

    return (
        <>
        <Link to="/final">
        <div className="container" >
            <Navbar12 />
            <Sidebar12 onMenuItemClick={handleMenuItemClick} />
            <Content12 selectedContent={selectedContent} />
            <LogoutConfirmation isOpen={isLogoutConfirmationOpen} onConfirm={handleLogoutConfirm} onClose={handleLogoutCancel} />
           
        </div>
        </Link>
          
        </>
      
    );
}

export default App12;
